$("#contactForm")
  .validator()
  .on("submit", function (event) {
    if (event.isDefaultPrevented()) {
      formError();
      submitMSG(false, "Por favor, preencha todos os campos corretamente.");
    } else {
      // Deixa o submit normal acontecer
      formSuccess();
    }
  });

function formSuccess() {
  submitMSG(true, "Formul√°rio enviado com sucesso!");
}

function formError() {
  $("#contactForm")
    .removeClass()
    .addClass("shake animated")
    .one(
      "webkitAnimationEnd mozAnimationEnd MSAnimationEnd oanimationend animationend",
      function () {
        $(this).removeClass();
      }
    );
}

function submitMSG(valid, msg) {
  const msgClasses = valid ? "h3 text-left tada animated text-success" : "h3 text-left text-danger";

  $("#msgSubmit").removeClass().addClass(msgClasses).text(msg);
}
